import React from "react";
import "./Testimonials.css";
import background from "../assets/pic11.png";

const testimonials = [
  {
    name: "Varunbharathi Janardhanam",
    role: "Manager, HCL Technologies",
    rating: 5.0,
    review:
      "Fahid is an enthusiastic, dedicated, and highly capable professional who consistently exceeds expectations. His strong technical skills, proactive mindset, and supportive nature make him a valued team member. With a proven track record of delivering quality results and improving processes, Fahid has the drive and potential to excel in any field he chooses. I strongly recommend him for further studies and future opportunities.",
  },
  {
    name: "Andrew",
    role: "Manager, Care & Cure",
    rating: 4.8,
    review:
      "Fahid is a sharp, dedicated, and intelligent student with excellent technical skills and strong analytical thinking. During his time in my classes on Computer Networking and Mobile Computing, he consistently impressed me with his curiosity and up-to-date knowledge. He actively participated in seminars and college activities and stood out for his ability to inspire peers. Fahid also successfully completed an image classification project using deep learning under my guidance, demonstrating strong practical skills. I confidently rank him among the top 10% of his class and highly recommend him for further studies and professional opportunities",
  },
];

const Testimonials = () => {
  return (
    <section
      className="testimonials"
      style={{ backgroundImage: `url(${background})` }}
    >
      <div className="header">
        <h2>
          Testimonials That <span>Speak to My Results</span>
        </h2>
      </div>
      <div className="testimonial-grid">
        {testimonials.map((testimonial, index) => (
          <div key={index} className="card">
            <div className="quote-icon">“</div>
            <div className="content">
              <img src={testimonial.avatar} alt={testimonial.name} />
              <h3>{testimonial.name}</h3>
              <p className="role">{testimonial.role}</p>
              <div className="rating">
                <span>⭐⭐⭐⭐⭐</span>
                {testimonial.rating}
              </div>
              <p className="review">{testimonial.review}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Testimonials;
